import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

const shine = keyframes`
  0% { background-position: 200% center; }
  100% { background-position: -200% center; }
`;

const Container = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 20px;
  background: linear-gradient(135deg, #0a192f 0%, #0d1b2a 100%);
`;

const Title = styled.h1`
  font-size: 3.5rem;
  margin-bottom: 2rem;
  color: white;
  text-align: center;
  background: linear-gradient(90deg, #00a3ff, #00ff87, #00a3ff);
  background-size: 200% auto;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: ${shine} 3s linear infinite;
`;

const DiscContainer = styled.div`
  width: 300px;
  height: 300px;
  position: relative;
  margin: 2rem 0;
`;

const Disc = styled.div`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  position: absolute;
  background: linear-gradient(45deg, #00a3ff, #00ff87);
  animation: ${rotate} 8s linear infinite;
  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 80%;
    height: 80%;
    background: #0a192f;
    border-radius: 50%;
  }
  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 20%;
    height: 20%;
    background: #00a3ff;
    border-radius: 50%;
    box-shadow: 0 0 20px #00a3ff;
  }
`;

const CallButton = styled.button`
  padding: 1rem 2.5rem;
  font-size: 1.2rem;
  background: linear-gradient(45deg, #00a3ff, #00ff87);
  border: none;
  border-radius: 30px;
  color: #0a192f;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 2rem;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(0, 163, 255, 0.4);
  }

  &:disabled {
    opacity: 0.7;
    cursor: not-allowed;
  }
`;

const StatusText = styled.p`
  color: #00ff87;
  font-size: 1.1rem;
  margin-top: 1rem;
  height: 1.5rem;
`;

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [status, setStatus] = useState('');

  const initiateCall = async () => {
    setIsLoading(true);
    setStatus('Initiating call...');
    try {
      const response = await axios.post('https://31ce-103-159-45-178.ngrok-free.app/make-call', {
        phoneNumber: '+919845503430'
      });
      if (response.data.success) {
        setStatus('Call initiated successfully!');
      } else {
        setStatus('Failed to initiate call');
      }
    } catch (error) {
      console.error('Error:', error);
      setStatus('Error initiating call');
    }
    setIsLoading(false);
  };

  return (
    <Container>
      <Title>Voice Assistant</Title>
      <DiscContainer>
        <Disc />
      </DiscContainer>
      <CallButton onClick={initiateCall} disabled={isLoading}>
        {isLoading ? 'Initiating...' : 'Call AI Agent'}
      </CallButton>
      <StatusText>{status}</StatusText>
    </Container>
  );
}

export default App;
