import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const Container = styled.div`
  background: linear-gradient(135deg, #1a1a1a 0%, #0a192f 100%);
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
`;

const DiscContainer = styled.div`
  position: relative;
  width: 300px;
  height: 300px;
  margin: 2rem;
`;

const Disc = styled.div`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  background: linear-gradient(45deg, #00a3ff, #0066ff);
  animation: ${rotate} 10s linear infinite;
  box-shadow: 0 0 30px rgba(0, 163, 255, 0.3);
  display: flex;
  align-items: center;
  justify-content: center;
`;

const CallButton = styled.button`
  padding: 1rem 2rem;
  font-size: 1.2rem;
  background: linear-gradient(45deg, #ff3366, #ff0066);
  border: none;
  border-radius: 25px;
  color: white;
  cursor: pointer;
  transition: transform 0.2s;
  margin-top: 2rem;

  &:hover {
    transform: scale(1.05);
  }
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  background: linear-gradient(45deg, #00a3ff, #ff3366);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

const Logo = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80px;
  height: 80px;
  background: white;
  border-radius: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

function App() {
  const [isLoading, setIsLoading] = useState(false);

  const initiateCall = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post('/make-call', {
        phoneNumber: '+919845503430' // Replace with your test number
      });
      alert('Call initiated successfully!');
    } catch (error) {
      console.error('Error initiating call:', error);
      alert('Failed to initiate call');
    }
    setIsLoading(false);
  };

  return (
    <Container>
      <Title>Voice Assistant</Title>
      <DiscContainer>
        <Disc>
          <Logo>
            <img src="/eleven-labs-logo.png" alt="Eleven Labs" width="60" />
          </Logo>
        </Disc>
      </DiscContainer>
      <CallButton onClick={initiateCall} disabled={isLoading}>
        {isLoading ? 'Initiating Call...' : 'Call AI Agent'}
      </CallButton>
    </Container>
  );
}

export default App;
